import React from 'react';
import { useSession } from '../contexts/SessionContext';
import { sessionAPI } from '../utils/api';

export default function AdminControls(){
  const [customNumber, setCustomNumber] = React.useState("");
  const [updating, setUpdating] = React.useState(false);
  const {session,user,activeStory,isRevealed,dispatch,votes,voteCount,totalMembers} = useSession();
  // Calculate vote count and total members for the current active story
  let submittedCount = 0;
  let memberCount = 0;
  if (session && session.members && activeStory && votes) {
    memberCount = session.members.length;
    submittedCount = session.members.filter(m => votes[m.id] !== undefined).length;
  }

  // Prevent rendering if required data is missing
  if (!session || !user || !activeStory) return null;

  return (
    <div className="admin-controls">
      {/* Show vote submitted count above the table */}
      {/* Show vote submitted count above the table */}
      {/* Centered Fancy Voting Status Table and Reveal Button */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        width: '100%',
      }}>
        <div style={{
          background: 'linear-gradient(90deg, #f8fafc 0%, #e0e7ef 100%)',
          borderRadius: '20px',
          boxShadow: '0 4px 24px rgba(0,0,0,0.13)',
          padding: '3.5vw',
          width: '100%',
          maxWidth: '1400px',
          minWidth: 'min(90vw, 600px)',
          border: '1px solid #e0e7ef',
          margin: '0 auto 2.5rem auto',
          transition: 'max-width 0.3s, padding 0.3s',
        }}>
          <div style={{display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:18}}>
            <span style={{fontWeight: 700, fontSize: '1.3rem', color: '#2d3a4a', letterSpacing: 1}}>Voting Status</span>
            <span style={{fontWeight:600, fontSize:'1.1rem', color:'#1976d2', textAlign:'right'}}>Votes Submitted: {submittedCount}/{memberCount}</span>
          </div>
          <table style={{width: '100%', borderCollapse: 'separate', borderSpacing: 0, background: 'white', borderRadius: 12, overflow: 'hidden', boxShadow: '0 1px 8px rgba(0,0,0,0.05)'}}>
            <thead style={{background: '#e0e7ef'}}>
              <tr>
                <th style={{padding: '16px 18px', textAlign: 'left', color: '#2d3a4a', fontWeight: 600, borderBottom: '2px solid #d1d5db', fontSize: '1.05rem'}}>Member</th>
                <th style={{padding: '16px 18px', textAlign: 'center', color: '#2d3a4a', fontWeight: 600, borderBottom: '2px solid #d1d5db', fontSize: '1.05rem'}}>Voted</th>
              </tr>
            </thead>
            <tbody>
              {session.members && session.members.map((member, idx) => (
                <tr key={member.id} style={{background: idx % 2 === 0 ? '#f8fafc' : 'white'}}>
                  <td style={{padding: '16px 18px', borderBottom: '1px solid #f1f5f9', fontSize: '1.05rem'}}>{member.name || member.username || member.id}</td>
                  <td style={{padding: '16px 18px', textAlign: 'center', borderBottom: '1px solid #f1f5f9', fontSize: '1.05rem'}}>
                    {votes && votes[member.id] !== undefined ? <span style={{color: '#22c55e', fontWeight: 700, fontSize: '1.2rem'}}>✔</span> : <span style={{color: '#ef4444', fontWeight: 700, fontSize: '1.2rem'}}>✗</span>}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <button
          onClick={async ()=>{
            await sessionAPI.revealVotes(session.id,activeStory.id,user.id);
          {/* Admin custom number update after reveal */}
          {isRevealed && user?.isAdmin && activeStory && (
            <div style={{marginBottom: 18, display:'flex', alignItems:'center', gap:8}}>
              <input
                type="number"
                value={customNumber}
                onChange={e => setCustomNumber(e.target.value)}
                placeholder="Set Card Number"
                style={{padding:'6px 12px', fontSize:'1rem', borderRadius:4, border:'1px solid #ccc', width:120}}
                min={0}
              />
              <button
                onClick={async () => {
                  setUpdating(true);
                  // Assume sessionAPI.updateStoryNumber(sessionId, storyId, newNumber) exists
                  await sessionAPI.updateStoryNumber(session.id, activeStory.id, customNumber);
                  // Refresh session data
                  const updatedSession = await sessionAPI.getSession(session.id);
                  const updatedStory = updatedSession.stories.find(s => s.id === activeStory.id);
                  dispatch({type:'SET', payload: { stories: updatedSession.stories, activeStory: updatedStory }});
                  setUpdating(false);
                }}
                disabled={updating || !customNumber}
                style={{padding:'6px 16px', background:'#6366f1', color:'#fff', border:'none', borderRadius:4, fontWeight:600, cursor: updating ? 'not-allowed' : 'pointer'}}
              >
                {updating ? 'Updating...' : 'Update Card Number'}
              </button>
            </div>
          )}
            // Fetch updated session to get revealed state and votes
            const updatedSession = await sessionAPI.getSession(session.id);
            // Update votes and isRevealed in context
            dispatch({type:'SET',payload:{votes: updatedSession.stories.find(s=>s.id===activeStory.id)?.votes || {}, isRevealed: true}});
          }}
          disabled={isRevealed}
          style={{
            marginTop: '1.2rem',
            padding: '0.3rem 1.1rem',
            fontSize: '0.95rem',
            fontWeight: 600,
            background: isRevealed ? '#a3a3a3' : 'linear-gradient(90deg, #3b82f6 0%, #6366f1 100%)',
            color: 'white',
            border: 'none',
            borderRadius: 6,
            boxShadow: '0 1px 4px rgba(0,0,0,0.08)',
            cursor: isRevealed ? 'not-allowed' : 'pointer',
            transition: 'background 0.2s',
            minWidth: 90
          }}
        >
          {isRevealed?'Revealed':'Reveal'}
        </button>
      </div>
    </div>
  );
}
