import React from 'react';
import { useSession } from '../contexts/SessionContext';

export default function MembersList(){
  const {session} = useSession();
  return (
    <div className="members-list">
      <h3 style={{fontWeight:700, fontSize:'1.1rem', color:'#2d3a4a', letterSpacing:1, marginBottom:10}}>Members ({session.members.length})</h3>
      <div style={{
        background: 'linear-gradient(90deg, #f8fafc 0%, #e0e7ef 100%)',
        borderRadius: '20px',
        boxShadow: '0 4px 24px rgba(0,0,0,0.13)',
        padding: '3.5vw',
        width: '100%',
        maxWidth: '1400px',
        minWidth: 'min(90vw, 600px)',
        border: '1px solid #e0e7ef',
        margin: '0 auto 2.5rem auto',
        transition: 'max-width 0.3s, padding 0.3s',
      }}>
        <table style={{width: '100%', borderCollapse: 'separate', borderSpacing: 0, background: 'white', borderRadius: 12, overflow: 'hidden', boxShadow: '0 1px 8px rgba(0,0,0,0.05)'}}>
          <thead style={{background: '#e0e7ef'}}>
            <tr>
              <th style={{padding: '16px 18px', textAlign: 'left', color: '#2d3a4a', fontWeight: 600, borderBottom: '2px solid #d1d5db', fontSize: '1.05rem'}}>Name</th>
              <th style={{padding: '16px 18px', textAlign: 'center', color: '#2d3a4a', fontWeight: 600, borderBottom: '2px solid #d1d5db', fontSize: '1.05rem'}}>Role</th>
            </tr>
          </thead>
          <tbody>
            {session.members.map((m, idx) => (
              <tr key={m.id} style={{background: idx % 2 === 0 ? '#f8fafc' : 'white'}}>
                <td style={{padding: '16px 18px', borderBottom: '1px solid #f1f5f9', fontSize: '1.05rem'}}>{m.name}</td>
                <td style={{padding: '16px 18px', textAlign: 'center', borderBottom: '1px solid #f1f5f9', fontSize: '1.05rem'}}>{m.isAdmin ? <span style={{color:'#6366f1',fontWeight:700}}>Admin</span> : 'Member'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
