import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { io } from 'socket.io-client';

const SessionContext = createContext();
const initialState = {
  session:null, user:null, socket:null,
  stories:[], activeStory:null,
  votes:{}, voteCount:0, totalMembers:0,
  isRevealed:false
};

function reducer(state,action){
  switch(action.type){
    case 'SET': return {...state,...action.payload};
    case 'ADD_MEMBER': return {
      ...state,
      session:{...state.session,members:[...state.session.members,action.payload]}
    };
    case 'ADD_STORY':
      return {...state,stories:[...state.stories,action.payload]};
    case 'SET_ACTIVE': return {...state,activeStory:action.payload};
    case 'SET_VOTE_COUNT':
      return {...state,voteCount:action.payload.voteCount,totalMembers:action.payload.totalMembers};
    case 'SET_VOTES': return {...state,votes:action.payload};
    case 'SET_REVEALED': return {...state,isRevealed:action.payload};
    case 'CLEAR_VOTES':
      return {...state,votes:{},voteCount:0,isRevealed:false};
    default: return state;
  }
}

export function SessionProvider({children}){
  const [state,dispatch] = useReducer(reducer,initialState);

  useEffect(()=>{
    const socket = io('http://localhost:5000');
    dispatch({type:'SET',payload:{socket}});

    socket.on('memberJoined',m=>dispatch({type:'ADD_MEMBER',payload:m}));
    socket.on('storyCreated',s=>dispatch({type:'ADD_STORY',payload:s}));
    socket.on('activeStoryChanged',id=>{
      const story = state.stories.find(s=>s.id===id);
      dispatch({type:'SET_ACTIVE',payload:story});
    });
    socket.on('voteCountChanged',data=>dispatch({type:'SET_VOTE_COUNT',payload:data}));
    socket.on('votesRevealed',data=>{
      dispatch({type:'SET_VOTES',payload:data.votes});
      dispatch({type:'SET_REVEALED',payload:true});
    });
    socket.on('votesCleared',()=>dispatch({type:'CLEAR_VOTES'}));

    return ()=>socket.disconnect();
  },[state.stories]);

  return (
    <SessionContext.Provider value={{...state,dispatch}}>
      {children}
    </SessionContext.Provider>
  );
}

export function useSession(){
  return useContext(SessionContext);
}
