import React, { useState } from 'react';
import { useSession } from '../contexts/SessionContext';
import { sessionAPI } from '../utils/api';

export default function StoryManager(){
  const {session,user,stories,activeStory,dispatch} = useSession();
  const [open,setOpen]=useState(false);
  const [title,setTitle]=useState('');
  const [desc,setDesc]=useState('');

  const handle = async e=>{
    e.preventDefault();
    if(!title) return;
    await sessionAPI.createStory(session.id,title,desc,user.id);
    setTitle('');setDesc('');setOpen(false);
  };

  return (
    <div className="story-manager">
      <h3>Stories</h3>
      {user.isAdmin && (
        <button onClick={()=>setOpen(!open)}>+ Add Story</button>
      )}
      {open && (
        <form onSubmit={handle}>
          <input 
            placeholder="Title"
            value={title}
            onChange={e=>setTitle(e.target.value)}
          />
          <textarea
            placeholder="Description"
            value={desc}
            onChange={e=>setDesc(e.target.value)}
          />
          <button type="submit">Create</button>
        </form>
      )}
      {stories.map(s=>(
        <div 
          key={s.id} 
          className={`story-item ${activeStory?.id===s.id?'active':''}`}
        >
          <h4>{s.title}</h4>
          {s.description && <p>{s.description}</p>}
          {activeStory?.id===s.id && <span>Active</span>}
        </div>
      ))}
    </div>
  );
}
