import React from 'react';
import { useSession } from '../contexts/SessionContext';
import { sessionAPI } from '../utils/api';

export default function AdminControls(){
  const {session,user,activeStory,isRevealed,dispatch,votes,voteCount,totalMembers} = useSession();
  // Force re-render on votes change
  React.useEffect(() => {}, [votes]);

  // Prevent rendering if required data is missing
  if (!session || !user || !activeStory) return null;

  return (
    <div className="admin-controls">
      <button 
        onClick={async ()=>{
          await sessionAPI.revealVotes(session.id,activeStory.id,user.id);
          // Fetch updated session to get revealed state and votes
          const updatedSession = await sessionAPI.getSession(session.id);
          // Update votes and isRevealed in context
          dispatch({type:'SET',payload:{votes: updatedSession.stories.find(s=>s.id===activeStory.id)?.votes || {}, isRevealed: true}});
        }}
        disabled={isRevealed}
      >
        {isRevealed?'Revealed':'Reveal Votes'}
      </button>

      {/* Voting Status Table */}
      <div style={{marginTop: '1rem'}}>
        <strong>Voting Status</strong>
        <table style={{width: '100%', borderCollapse: 'collapse', marginTop: '0.5rem'}}>
          <thead>
            <tr>
              <th style={{border: '1px solid #ccc', padding: '4px'}}>Member</th>
              <th style={{border: '1px solid #ccc', padding: '4px'}}>Voted</th>
            </tr>
          </thead>
          <tbody>
            {session.members && session.members.map(member => (
              <tr key={member.id}>
                <td style={{border: '1px solid #ccc', padding: '4px'}}>{member.name || member.username || member.id}</td>
                <td style={{border: '1px solid #ccc', padding: '4px'}}>
                  {votes && votes[member.id] !== undefined ? '✅' : '❌'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div style={{marginTop: '0.5rem'}}>
          <strong>Vote Count:</strong> {voteCount} / {totalMembers}
        </div>
      </div>
    </div>
  );
}
