import React, { useState } from 'react';
import { useSession } from '../contexts/SessionContext';
import { sessionAPI } from '../utils/api';

const POINTS=[1,2,3,5,8,13,21,'?'];

export default function VotingBoard(){
  const {session,user,activeStory,votes,isRevealed,voteCount,totalMembers} = useSession();
  const [sel,setSel]=useState(null);
  const prevStoryId = React.useRef(activeStory?.id);

  // Clear selection when story changes
  React.useEffect(() => {
    if (activeStory?.id !== prevStoryId.current) {
      setSel(null);
      prevStoryId.current = activeStory?.id;
    }
  }, [activeStory?.id]);

  const { dispatch } = useSession();
  const vote = async p => {
    if (!activeStory || isRevealed) return;
    setSel(p);
    // Optimistically update votes for instant feedback
    dispatch({ type: 'SET', payload: { votes: { ...votes, [user.id]: p } } });
    await sessionAPI.vote(session.id, user.id, activeStory.id, p);
  };

  const avg=(()=>{
    const nums=Object.values(votes).filter(v=>typeof v==='number');
    return nums.length
      ?(nums.reduce((a,b)=>a+b,0)/nums.length).toFixed(1)
      :0;
  })();

  if(!activeStory) return (
    <div className="voting-board">
      <h2>No Active Story</h2>
    </div>
  );

  return (
    <div className="voting-board">
      <h2>{activeStory.title}</h2>
      {activeStory.description && <p>{activeStory.description}</p>}
      <div>Votes: {voteCount}/{totalMembers}</div>

      {!isRevealed ? (
        <>
          <div className="cards">
            {POINTS.map(p=>(
              <button
                key={p}
                className={sel===p?'selected':''}
                onClick={()=>vote(p)}
              >{p}</button>
            ))}
          </div>
          <div style={{marginTop:'1em'}}>
            <h4>Who has voted</h4>
            <table style={{width:'100%',borderCollapse:'separate',borderSpacing:'0 8px',background:'#fff',boxShadow:'0 2px 8px #eee',borderRadius:'8px'}}>
              <thead>
                <tr>
                  <th style={{padding:'8px',textAlign:'left',fontWeight:'bold',fontSize:'1em',borderBottom:'2px solid #f0f0f0'}}>User</th>
                  <th style={{padding:'8px',textAlign:'center',fontWeight:'bold',fontSize:'1em',borderBottom:'2px solid #f0f0f0'}}>Status</th>
                </tr>
              </thead>
              <tbody>
                {session.members.map(m=>(
                  <tr key={m.id} style={{background:'#fafbfc'}}>
                    <td style={{padding:'8px',display:'flex',alignItems:'center',gap:'10px',borderBottom:'1px solid #f0f0f0'}}>
                      <span style={{display:'inline-block',width:'28px',height:'28px',borderRadius:'50%',background:'#e0e7ef',display:'flex',alignItems:'center',justifyContent:'center',fontSize:'1.2em'}}>
                        <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="#6c7a89" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><circle cx="12" cy="7" r="4"/><path d="M5.5 21a8.38 8.38 0 0 1 13 0"/></svg>
                      </span>
                      <span>{m.name}</span>
                    </td>
                    <td style={{padding:'8px',textAlign:'center',borderBottom:'1px solid #f0f0f0'}}>
                      {votes[m.id]!==undefined ? (
                        <span style={{display:'inline-flex',alignItems:'center',gap:'6px'}}>
                          <span style={{display:'inline-block',width:'14px',height:'14px',borderRadius:'50%',background:'#2ecc40',border:'2px solid #2ecc40',marginRight:'4px'}}></span>
                          <span style={{color:'#2ecc40',fontWeight:'bold'}}>Voted</span>
                        </span>
                      ) : (
                        <span style={{display:'inline-flex',alignItems:'center',gap:'6px'}}>
                          <span style={{display:'inline-block',width:'14px',height:'14px',borderRadius:'50%',background:'#ff4136',border:'2px solid #ff4136',marginRight:'4px'}}></span>
                          <span style={{color:'#ff4136',fontWeight:'bold'}}>Not Voted</span>
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      ) : (
        <div className="results">
          <div>Average: {avg}</div>
          <table style={{marginTop:'1em',width:'100%',borderCollapse:'collapse'}}>
            <thead>
              <tr>
                <th style={{border:'1px solid #ccc',padding:'4px'}}>User</th>
                <th style={{border:'1px solid #ccc',padding:'4px'}}>Vote</th>
              </tr>
            </thead>
            <tbody>
              {session.members.map(m=>(
                <tr key={m.id}>
                  <td style={{border:'1px solid #ccc',padding:'4px'}}>{m.name}</td>
                  <td style={{border:'1px solid #ccc',padding:'4px'}}>{votes[m.id]!==undefined?votes[m.id]:'No vote'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
