import React from 'react';
import { useSession } from '../contexts/SessionContext';
import { sessionAPI } from '../utils/api';

export default function AdminControls(){
  const {session,user,activeStory,isRevealed,dispatch,votes} = useSession();
  // Force re-render on votes change
  React.useEffect(() => {}, [votes]);

  // Prevent rendering if required data is missing
  if (!session || !user || !activeStory) return null;

  return (
    <div className="admin-controls">
      {/* Fancy Voting Status Table above the reveal button */}
      <div style={{
        marginBottom: '1.5rem',
        background: 'linear-gradient(90deg, #f8fafc 0%, #e0e7ef 100%)',
        borderRadius: '12px',
        boxShadow: '0 2px 8px rgba(0,0,0,0.07)',
        padding: '1rem',
        maxWidth: 420,
        margin: '0 auto 1.5rem auto',
        border: '1px solid #e0e7ef'
      }}>
        <div style={{fontWeight: 700, fontSize: '1.1rem', marginBottom: 10, color: '#2d3a4a', letterSpacing: 1}}>Voting Status</div>
        <table style={{width: '100%', borderCollapse: 'separate', borderSpacing: 0, background: 'white', borderRadius: 8, overflow: 'hidden', boxShadow: '0 1px 4px rgba(0,0,0,0.03)'}}>
          <thead style={{background: '#e0e7ef'}}>
            <tr>
              <th style={{padding: '8px 12px', textAlign: 'left', color: '#2d3a4a', fontWeight: 600, borderBottom: '1px solid #d1d5db'}}>Member</th>
              <th style={{padding: '8px 12px', textAlign: 'center', color: '#2d3a4a', fontWeight: 600, borderBottom: '1px solid #d1d5db'}}>Voted</th>
            </tr>
          </thead>
          <tbody>
            {session.members && session.members.map((member, idx) => (
              <tr key={member.id} style={{background: idx % 2 === 0 ? '#f8fafc' : 'white'}}>
                <td style={{padding: '8px 12px', borderBottom: '1px solid #f1f5f9'}}>{member.name || member.username || member.id}</td>
                <td style={{padding: '8px 12px', textAlign: 'center', borderBottom: '1px solid #f1f5f9'}}>
                  {votes && votes[member.id] !== undefined ? <span style={{color: '#22c55e', fontWeight: 700}}>✔</span> : <span style={{color: '#ef4444', fontWeight: 700}}>✗</span>}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <button 
        onClick={async ()=>{
          await sessionAPI.revealVotes(session.id,activeStory.id,user.id);
          // Fetch updated session to get revealed state and votes
          const updatedSession = await sessionAPI.getSession(session.id);
          // Update votes and isRevealed in context
          dispatch({type:'SET',payload:{votes: updatedSession.stories.find(s=>s.id===activeStory.id)?.votes || {}, isRevealed: true}});
        }}
        disabled={isRevealed}
        style={{
          display: 'block',
          margin: '0 auto',
          padding: '0.7rem 2.2rem',
          fontSize: '1.1rem',
          fontWeight: 600,
          background: isRevealed ? '#a3a3a3' : 'linear-gradient(90deg, #3b82f6 0%, #6366f1 100%)',
          color: 'white',
          border: 'none',
          borderRadius: 8,
          boxShadow: '0 1px 4px rgba(0,0,0,0.08)',
          cursor: isRevealed ? 'not-allowed' : 'pointer',
          transition: 'background 0.2s'
        }}
      >
        {isRevealed?'Revealed':'Reveal Votes'}
      </button>
    </div>
  );
}
